chapter.programlisting:
@Entity
public class Dog {
   @Id @GeneratedValue(strategy = GenerationType.TABLE, generator = "dog")
   @TableGenerator(
      name = "dog",
      table = "sequences",
      pkColumnName = "key",
      pkColumnValue = "dog",
      valueColumnName = "seed"
   )
   public Long getId() { return id; }
   public void setId(Long id) { this.id = id; }
   private Long id;

   public String getName() { return name; }
   public void setName(String name) { this.name = name; }
   private String name;

   @ManyToOne
   public Breed getBreed() { return breed; }
   public void setBreed(Breed breed) { this.breed = breed; }
   private Breed breed;
}

@Entity
public class Breed {

   @Id @GeneratedValue(generator = "uuid")
   @GenericGenerator(name="uuid", strategy="uuid2")
   public String getId() { return id; }
   public void setId(String id) { this.id = id; }
   private String id;

   public String getName() { return name; }
   public void setName(String name) { this.name = name; }
   private String name;
}

//accessing JBoss's Transaction can be done differently but this one works nicely
TransactionManager tm = new JBossStandAloneJtaPlatform().getTransactionManager();

//build the EntityManagerFactory as you would build in in Hibernate ORM
EntityManagerFactory emf = Persistence.createEntityManagerFactory(
    "org.hibernate.ogm.tutorial.jpa");

final Logger logger = LoggerFactory.getLogger(DogBreedRunner.class);

[...]

//Persist entities the way you are used to in plain JPA
tm.begin();
EntityManager em = emf.createEntityManager();
Breed collie = new Breed();
collie.setName("Collie");
em.persist(collie);
Dog dina = new Dog();
dina.setName("Dina");
dina.setBreed(collie);
em.persist(dina);
Long dinaId = dina.getId();
em.flush();
em.close();
tm.commit();

[...]

//Retrieve your entities the way you are used to in plain JPA
tm.begin();
em = emf.createEntityManager();
dina = em.find(Dog.class, dinaId);
logger.debug(dina.getName());
logger.debug(dina.getBreed().getName());
em.flush();
em.close();
tm.commit();

[...]
emf.close();;
